import React, { useRef, useState } from "react";
import axios from "axios";

const Insert = () => {
  const txtname = useRef();
  const txtemail = useRef();
  const txtpassword = useRef();
  const [dataList, setDataList] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const name = txtname.current.value;
    const email = txtemail.current.value;
    const password = txtpassword.current.value;

    const formData = new FormData();
    formData.set("name", name);
    formData.set("email", email);
    formData.set("password", password);

    axios
      .post("https://geton.skmbpk1z.a2hosted.com/insert-data.php", formData)
      .then(() => {
        // Clear form inputs
        txtname.current.value = "";
        txtemail.current.value = "";
        txtpassword.current.value = "";

        // Fetch updated data (assuming the server provides an endpoint to get all data)
        axios
          .get("https://geton.skmbpk1z.a2hosted.com/get-data.php")
          .then((response) => {
            setDataList(response.data); // Update state with fetched data
          })
          .catch((error) => {
            console.error("Error fetching data:", error);
          });
      })
      .catch((error) => {
        console.error("Error submitting data:", error);
      });
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <div>
        <table
          border={1}
          cellPadding={7}
          cellSpacing={0.1}
          style={{ marginBottom: "20px" }}
        >
          <tbody>
            <tr>
              <td>Name</td>
              <td>
                <input type="text" ref={txtname} />
              </td>
            </tr>
            <tr>
              <td>Email</td>
              <td>
                <input type="text" ref={txtemail} />
              </td>
            </tr>
            <tr>
              <td>Password</td>
              <td>
                <input type="text" ref={txtpassword} />
              </td>
            </tr>
            <tr>
              <td colSpan={2}>
                <button onClick={handleSubmit}>Submit</button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>

      {dataList.length > 0 && (
        <div>
          <h3>Submitted Data</h3>
          <table border={1} cellPadding={7} cellSpacing={0.1}>
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Password</th>
              </tr>
            </thead>
            <tbody>
              {dataList.map((item, index) => (
                <tr key={index}>
                  <td>{item.name}</td>
                  <td>{item.email}</td>
                  <td>{item.password}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Insert;
