import React, { useEffect, useState } from "react";
import axios from "axios";

const Delete = () => {
  const [data, setData] = useState([]);

  // Fetch data on component mount
  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    axios
      .get("https://geton.skmbpk1z.a2hosted.com/get-data.php")
      .then((res) => {
        setData(res.data);
      })
      .catch((err) => {
        console.error("Fetch error:", err);
      });
  };

  const handleDelete = (id) => {
    const formData = new FormData();
    formData.set("id", id);

    axios
      .post("https://geton.skmbpk1z.a2hosted.com/delete-data.php", formData)
      .then((res) => {
        if (res.data.status === "true") {
          fetchData(); // Refresh table after deletion
        }
      })
      .catch((err) => {
        console.error("Delete error:", err);
      });
  };

  return (
    <>
      <h2>Delete Data</h2>
      <table border={1} cellPadding={5} cellSpacing={0}>
        <thead>
          <tr>
            <th>id</th>
            <th>name</th>
            <th>email</th>
            <th>password</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.email}</td>
              <td>{item.password}</td>
              <td>
                <button onClick={() => handleDelete(item.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default Delete;
