import React, { useRef, useState, useEffect } from "react";
import axios from "axios";

const Insert = () => {
    const [userData, setUserData] = useState([]);
    var txtname = useRef();
    var txtemail = useRef();
    var txtpassword = useRef();
    
    // Load data when component mounts
    useEffect(() => {
        loadData();
    }, []);
    
    // Function to load data from server
    const loadData = () => {
        axios.get('https://geton.skmbpk1z.a2hosted.com/get-data.php')
            .then(response => {
                setUserData(response.data);
            })
            .catch(error => {
                console.error("Error loading data:", error);
            });
    };
    
    const handlesubmit = (e) => {
        e.preventDefault();
        var name = txtname.current.value;
        var email = txtemail.current.value;
        var password = txtpassword.current.value;
        var a = new FormData()
        a.set('name', name)
        a.set('email', email)
        a.set('password', password)
        
        axios.post('https://geton.skmbpk1z.a2hosted.com/insert-data.php', a).then(function(response) {
            // Clear form inputs after successful submission
            txtname.current.value = '';
            txtemail.current.value = '';
            txtpassword.current.value = '';
            
            // Reload data to update the table
            loadData();
        })
    }
    
    return (
        <>
            <form method="post" onSubmit={handlesubmit}>
                <table border={1} cellPadding={7} cellSpacing={0.1}>
                    <tbody>
                        <tr>
                            <td>Name</td>
                            <td><input type="text" ref={txtname}/></td>
                        </tr>
                        <tr>
                            <td>Email</td>
                            <td><input type="text" ref={txtemail}/></td>
                        </tr>
                        <tr>
                            <td>Password</td>
                            <td><input type="text" ref={txtpassword}/></td>
                        </tr>
                        <tr>
                            <td>
                                <input type="submit" value={'submit'}/>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </form>
            
            <h3>User Data</h3>
            <table border={1} cellPadding={7} cellSpacing={0.1}>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Password</th>
                    </tr>
                </thead>
                <tbody>
                    {userData.map((user, index) => (
                        <tr key={index}>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.password}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </>
    )
}

export default Insert
