import React, { useEffect, useState } from "react";
import axios from "axios";

const Delete = () => {
    const [data, setData] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = () => {
        axios.get('https://geton.skmbpk1z.a2hosted.com/get-data.php')
            .then(function (abc) {
                setData(abc.data);
            });
    };

    const handleDelete = (e) => {
        const id = e.target.getAttribute('data');
        const formData = new FormData();
        formData.set('id', id);

        axios.post('https://geton.skmbpk1z.a2hosted.com/delete.php', formData)
            .then(function (res) {
                if (res.data.status === "true") {
                    fetchData(); // Refresh data after delete
                } else {
                    alert("Failed to delete");
                }
            });
    };

    return (
        <>
            <table border={1} cellPadding={3} cellSpacing={0}>
                <thead>
                    <tr>
                        <th>id</th>
                        <th>name</th>
                        <th>email</th>
                        <th>password</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((e) => (
                            <tr key={e.id}>
                                <td>{e.id}</td>
                                <td>{e.name}</td>
                                <td>{e.email}</td>
                                <td>{e.password}</td>
                                <td>
                                    <button onClick={handleDelete} data={e.id}>Delete</button>
                                </td>
                            </tr>
                        ))
                    }
                </tbody>
            </table>
        </>
    );
};

export default Delete;
