import React, { useRef } from "react";
import axios from "axios";

const Insert = ({ fetchdata }) => {
  const txtname = useRef();
  const txtemail = useRef();
  const txtpassword = useRef();

  const handlesubmit = (e) => {
    e.preventDefault();

    const name = txtname.current.value;
    const email = txtemail.current.value;
    const password = txtpassword.current.value;

    const formData = new FormData();
    formData.set("name", name);
    formData.set("email", email);
    formData.set("password", password);

    axios
      .post("https://geton.skmbpk1z.a2hosted.com/insert-data.php", formData)
      .then(() => {
        txtname.current.value = "";
        txtemail.current.value = "";
        txtpassword.current.value = "";
        fetchdata(); // Refresh data in parent
      });
  };

  return (
    <form onSubmit={handlesubmit}>
      <h3>Insert New Data</h3>
      <table border={1} cellPadding={7}>
        <tbody>
          <tr>
            <td>Name</td>
            <td><input type="text" ref={txtname} required /></td>
          </tr>
          <tr>
            <td>Email</td>
            <td><input type="email" ref={txtemail} required /></td>
          </tr>
          <tr>
            <td>Password</td>
            <td><input type="text" ref={txtpassword} required /></td>
          </tr>
          <tr>
            <td colSpan={2}>
              <button type="submit">Insert</button>
            </td>
          </tr>
        </tbody>
      </table>
    </form>
  );
};

export default Insert;
