import React, { useRef, useState, useEffect } from "react";
import axios from "axios";

const Insert = () => {
  const txtname = useRef();
  const txtemail = useRef();
  const txtpassword = useRef();
  const [data, setData] = useState([]);

  const fetchData = () => {
    axios
      .get("https://geton.skmbpk1z.a2hosted.com/select-data.php")
      .then((res) => {
        setData(res.data);
      })
      .catch((err) => {
        console.error("Fetch error:", err);
      });
  };

  useEffect(() => {
    fetchData(); // Fetch on first load
  }, []);

  const handlesubmit = (e) => {
    e.preventDefault();

    const name = txtname.current.value;
    const email = txtemail.current.value;
    const password = txtpassword.current.value;

    const formData = new FormData();
    formData.set("name", name);
    formData.set("email", email);
    formData.set("password", password);

    axios
      .post("https://geton.skmbpk1z.a2hosted.com/insert-data.php", formData)
      .then((res) => {
        console.log("Inserted successfully:", res.data);
        txtname.current.value = "";
        txtemail.current.value = "";
        txtpassword.current.value = "";

        fetchData(); // Fetch updated data without refresh
      })
      .catch((err) => {
        console.error("Error:", err);
      });
  };

  return (
    <>
      <form onSubmit={handlesubmit}>
        <table border={1} cellPadding={7}>
          <tbody>
            <tr>
              <td>Name</td>
              <td>
                <input type="text" ref={txtname} />
              </td>
            </tr>
            <tr>
              <td>Email</td>
              <td>
                <input type="text" ref={txtemail} />
              </td>
            </tr>
            <tr>
              <td>Password</td>
              <td>
                <input type="text" ref={txtpassword} />
              </td>
            </tr>
            <tr>
              <td colSpan={2}>
                <button type="submit">Submit</button>
              </td>
            </tr>
          </tbody>
        </table>
      </form>

      <h3>Inserted Data</h3>
      <table border={1} cellPadding={7}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Password</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              <td>{row.name}</td>
              <td>{row.email}</td>
              <td>{row.password}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default Insert;
